mapper.select:
SELECT id, user_id, score, comment, is_waited, character_id, created_at, total_kcal, total_carb_g, total_protein_g, total_fat_g, total_sugar_g, total_natrium_g FROM reports WHERE user_id = #{userId}
AND created_at >= #{startDate}
AND created_at < DATE_ADD(#{endDate}, INTERVAL 1 DAY)
ORDER BY created_at DESC
LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO meal_foods (
meal_id, food_code, user_food_code, eated_weight
) VALUES (
#{mealId},
#{foodCode}, 
#{userFoodCode},
#{eatedWeight}
)
