mapper.update:
UPDATE users
name = #{name}, password = #{password}, age = #{age}, height = #{height}, weight = #{weight}, gender = #{gender}, activity_level = #{activityLevel}, is_diabetes = #{isDiabetes}, role = #{role},
WHERE id = #{id}

UPDATE std_infos
std_weight = #{stdWeight}, std_kcal = #{stdKcal}, std_carb_g = #{stdCarb}, std_protein_g = #{stdProtein}, std_fat_g = #{stdFat}, std_sugar_g = #{stdSugar}, std_natrium_g = #{stdNatrium},
WHERE user_id = #{userId}
